INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005241, N'مانع الحركة (السيارات)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز إلكتروني يمنع تشغيل محرك السيارة أثناء محاولة السرقة. يشمل الأجهزة التي تتطلب مفتاحًا لتوفير نبضة مشفرة لجهاز منع الحركة مما يسمح بتشغيل المحرك.', N'باستثناء أجهزة الإنذار والتتبع.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003755, N'أجهزة الإغلاق', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز يتم تشغيله بواسطة مفتاح أو مجموعة أو بطاقة مفتاح ويستخدم لإغلاق أو تأمين مكونات السيارة. تشمل المنتجات أقفال العمود وأقفال الإطارات الاحتياطية وأقفال عجلة القيادة وأقفال الدواسة.', N'باستثناء منتجات مثل أجهزة إنذار السيارة ومفاتيح / أقفال السيارة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003008, N'أجهزة تتبع', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز إلكتروني يسمح بتعقب السيارة في حالة سرقتها أو ضياعها.', N'يستثنى من ذلك منتجات مثل أجهزة إنذار السيارات المجهزة بأجهزة تتبع كميزة إضافية وأجهزة قفل مستخدمة في المركبات.')
