INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002773, N'العناية بعجلات السيارة - المنظفات / اللمعان', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مستحضر مصمم خصيصًا لتنظيف عجلة السيارة ولإضفاء اللمعان واللمعان.', N'باستثناء ضمادات الإطارات / اللمعان والمنتجات الأخرى غير المصممة خصيصًا لعجلات السيارات.')
