INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002734, N'رفوف / ناقلات المعدات (السيارات)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كإطار ، مثبتة بالسقف أو صندوق الأمتعة أو الباب الخلفي للسيارة ومصممة خصيصًا لنقل البضائع ، مثل الإطارات أو الدراجات أو الزوارق أو السلالم أو الزلاجات أو ألواح التزلج على الماء. يتضمن رفوفًا مصممة خصيصًا لنقل البنادق بأمان ، مثل المقعد السفلي ، المقعد الخلفي ، أرفف مسدسات السيارة الأرضية.', N'يستثنى من ذلك جميع الحوامل غير المصممة خصيصًا لمركبات السيارات.')
