INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002748, N'صناديق تخزين داخلية / صواني / سلال (سيارات)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حاوية ، مُصممة بأربعة جوانب متعامدة على القاعدة وغالبًا ما يكون لها غطاء أو أغطية ، مصممة خصيصًا لتخزين الأشياء في السيارة. يتضمن صناديق أدوات السيارة ومنظمات التمهيد.', N'يستثنى من ذلك جميع الحاويات الأخرى غير المصممة خصيصًا لمركبات السيارات.')
