INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005818, N'حواجز العجلات (السيارات)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها كتل مثلثة توضع تحت عجلات سيارة أو شاحنة لمنع السيارة من التحرك عند تحميل / تفريغ حمولة ثقيلة. تحتوي هذه المنتجات عادةً على وسادات جر مانعة للانزلاق أسفل الكتل ويمكن تصميمها بأقواس تثبيت اختيارية.', N'يستثنى من ذلك المنتجات مثل أذرع العجلات المستخدمة في الطيران وغيرها من معدات العجلات المصنفة.')
