INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003774, N'مرايا (سيارات)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز مصمم خصيصًا ليتم تثبيته داخل السيارة للرؤية الخلفية أو تلك التي تعلق على الجزء الخلفي من حاجب. تشمل المنتجات تلك التي تحتوي على ميزات إضافية ، مثل الساعة. تشتمل أيضًا على مرايا ذات تعتيم تلقائي مزودة بأجهزة استشعار للتعرف على الضوء الخلفي الذي يقترب وإزالته.', N'باستثناء المرايا الداخلية غير المصممة خصيصًا لسلامة السيارة.')
