INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006376, N'حماية وقوف السيارات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز يمكن وضعها على الحائط أو على عمود لحماية السيارة وقد تعزز رؤية العائق للسائق. يشمل على وجه التحديد صندوقًا مستطيلًا أو أي شكل من الأشكال البيضاء والحمراء المجردة ، المصمم لمنع خدش السيارة في المرآب أثناء مناورة وقوف السيارات.', N'باستثناء العناصر التي يتم تركيبها على السيارة. يستثنى من ذلك لافتات الطريق ، العناصر غير المصممة خصيصًا لحماية البيئة من السيارة.')
